// Parse webhook body if it's a string
const webhookBody = $('Webhook - Filtrar Candidato').first().json.body;
const webhookData = typeof webhookBody === 'string' ? JSON.parse(webhookBody) : webhookBody;

const openaiResp = $('OpenAI - Puntuar y Generar Preguntas').first().json.output?.[0]?.content?.[0]?.text;

// Clean markdown code blocks and extra text from OpenAI response
let cleanedResp = openaiResp || '';

// Remove markdown code blocks
cleanedResp = cleanedResp.replace(/```json\s*/g, '').replace(/```\s*/g, '');

// Try to extract JSON from the response if it contains extra text
const jsonMatch = cleanedResp.match(/\{[\s\S]*\}/);
if (jsonMatch) {
  cleanedResp = jsonMatch[0];
}

cleanedResp = cleanedResp.trim();

// Parse AI response
let aiData;
try {
  aiData = cleanedResp ? JSON.parse(cleanedResp) : {};
} catch (error) {
  throw new Error('Failed to parse OpenAI response as JSON. Response: ' + cleanedResp.substring(0, 200));
}

const questions = aiData.questions || [];

// Return questions without entrevista_id (will be added by frontend)
return questions.map((q, index) => ({
  tipo: q.tipo || 'abierta',
  contenido: q.contenido || q.question || '',
  generada_por: 'ia'
}));

const webhookData = $('Webhook - Recibir Respuestas').first().json.body ? JSON.parse($('Webhook - Recibir Respuestas').first().json.body) : $('Webhook - Recibir Respuestas').first().json;
const openaiResp = $('OpenAI - Evaluar Respuestas').first().json.output?.[0]?.content?.[0]?.text;

// Clean markdown code blocks from OpenAI response
let cleanedResp = openaiResp || '';
if (cleanedResp.includes('```json')) {
  cleanedResp = cleanedResp.replace(/```json\s*/g, '').replace(/```\s*/g, '');
}

// Improved JSON extraction with better error handling
let aiEvaluation = {};

try {
  // First attempt: Try to extract JSON between curly braces using a more robust regex
  const jsonMatch = cleanedResp.match(/\{[\s\S]*\}/g);

  if (jsonMatch && jsonMatch.length > 0) {
    // Try to parse the first (or largest) JSON object found
    let parsed = false;
    for (const match of jsonMatch) {
      try {
        aiEvaluation = JSON.parse(match.trim());
        parsed = true;
        break;
      } catch (e) {
        // Continue to next match
        continue;
      }
    }

    // If no match was successfully parsed, try parsing the entire cleaned response
    if (!parsed) {
      aiEvaluation = JSON.parse(cleanedResp.trim());
    }
  } else {
    // No JSON pattern found, try parsing the entire response
    aiEvaluation = JSON.parse(cleanedResp.trim());
  }
} catch (error) {
  // If all parsing attempts fail, use safe default values
  console.log('Failed to parse OpenAI response, using default values. Error:', error.message);
  console.log('OpenAI response was:', openaiResp?.substring(0, 200));

  aiEvaluation = {
    evaluaciones: [],
    naturalidad: 0,
    coherencia: 0,
    ai_detected: false,
    ai_confidence: 0,
    puntaje_promedio: 0
  };
}

const entrevistaId = webhookData.entrevista_id;
const respuestas = webhookData.respuestas || [];

// Procesar respuestas para guardar en la tabla respuestas
const respuestasParaGuardar = respuestas.map((resp, index) => ({
  pregunta_id: resp.pregunta_id,
  contenido: resp.contenido || resp.respuesta || '',
  tipo: resp.tipo || 'texto'
}));

// Calcular puntaje final
const evaluaciones = aiEvaluation.evaluaciones || [];
const naturalidad = aiEvaluation.naturalidad || 0;
const coherencia = aiEvaluation.coherencia || 0;
const aiDetected = aiEvaluation.ai_detected || false;
const aiConfidence = aiEvaluation.ai_confidence || 0;
const puntajePromedio = aiEvaluation.puntaje_promedio || 0;

let puntajeFinal = puntajePromedio || ((naturalidad * 0.4) + (coherencia * 0.6));

// Aplicar penalizaciones
if (aiDetected && aiConfidence > 70) {
  puntajeFinal -= 30;
}

if (respuestas.some(r => (r.contenido || r.respuesta || '').length < 50)) {
  puntajeFinal -= 20;
}

if (!respuestas.some(r => (r.contenido || r.respuesta || '').includes('ejemplo') || (r.contenido || r.respuesta || '').includes('experiencia'))) {
  puntajeFinal -= 15;
}

puntajeFinal = Math.max(0, Math.min(100, puntajeFinal));

// Preparar puntajes para guardar - solo si existen evaluaciones
const puntajes = [];

if (evaluaciones && evaluaciones.length > 0) {
  // Calcular promedios de naturalidad y coherencia de todas las evaluaciones
  let totalNaturalidad = 0;
  let totalCoherencia = 0;
  let count = 0;

  evaluaciones.forEach(eval => {
    totalNaturalidad += eval.naturalidad || 0;
    totalCoherencia += eval.coherencia || 0;
    count++;
  });

  const promedioNaturalidad = count > 0 ? Math.round(totalNaturalidad / count) : 0;
  const promedioCoherencia = count > 0 ? Math.round(totalCoherencia / count) : 0;

  // Crear entradas de puntajes sin pregunta_id
  puntajes.push({
    entrevista_id: entrevistaId,
    criterio: 'naturalidad',
    valor: promedioNaturalidad
  });

  puntajes.push({
    entrevista_id: entrevistaId,
    criterio: 'coherencia',
    valor: promedioCoherencia
  });

  puntajes.push({
    entrevista_id: entrevistaId,
    criterio: 'deteccion_ia',
    valor: Math.round(aiConfidence)
  });
}

return {
  entrevista_id: entrevistaId,
  respuestas: respuestasParaGuardar,
  puntajes: puntajes,
  puntaje_final: Math.round(puntajeFinal),
  ai_detected: aiDetected,
  ai_confidence: aiConfidence
};
